import React, { useEffect, useState } from 'react';
import EditBook from './EditBook';

const ManageBooks = () => {
  const [books, setBooks] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [editingBook, setEditingBook] = useState(null);
  const [sortBy, setSortBy] = useState('title');
  const [sortOrder, setSortOrder] = useState('asc');

  const userId = Number(localStorage.getItem('user_id'));

  const fetchBooks = async () => {
    setLoading(true);
    try {
      const res = await fetch(
        `http://localhost/book-management-system/server/books/get.php?page=${page}&sortBy=${sortBy}&sortOrder=${sortOrder}`
      );
      const data = await res.json();
      setBooks(data.books);
      setTotalPages(data.totalPages);
      setError('');
    } catch (err) {
      setError('Failed to fetch books');
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchBooks();
    // eslint-disable-next-line
  }, [page, sortBy, sortOrder]);

  const handleDelete = async (bookId) => {
    if (window.confirm('Are you sure you want to delete this book?')) {
      try {
        const response = await fetch('http://localhost/book-management-system/server/books/delete.php', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ book_id: bookId, user_id: userId }),
        });

        const result = await response.json();
        if (response.ok && result.status === 'success') {
          alert('Book deleted successfully');
          setBooks(books.filter(book => book.id !== bookId));
        } else {
          alert(result.message || 'Failed to delete book');
        }
      } catch (err) {
        alert('Error deleting book');
      }
    }
  };

  const handleEditClick = (book) => {
    setEditingBook(book);
  };

  return (
    <div className="container mt-5">
      <h3 className="mb-3">Manage Books</h3>
      <button className="btn btn-success mb-3" onClick={() => setEditingBook({})}>Add Book</button>
      <div className="mb-3">
        <label>Sort by:</label>
        <select value={sortBy} onChange={e => setSortBy(e.target.value)} className="form-select w-auto d-inline ms-2">
          <option value="title">Title</option>
          <option value="genre">Genre</option>
          <option value="author_name">Author</option>
        </select>
        <select value={sortOrder} onChange={e => setSortOrder(e.target.value)} className="form-select w-auto d-inline ms-2">
          <option value="asc">Ascending</option>
          <option value="desc">Descending</option>
        </select>
      </div>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p className="text-danger">{error}</p>
      ) : (
        <>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Title</th>
                <th>Author</th>
                <th>Genre</th>
                <th>Description</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {books.map(book => (
                <tr key={book.id}>
                  <td>{book.title}</td>
                  <td>{book.author_name}</td>
                  <td>{book.genre}</td>
                  <td>{book.description}</td>
                  <td>
                    {userId && book.added_by === userId ? (
                      <>
                        <button className="btn btn-warning btn-sm me-2" onClick={() => handleEditClick(book)}>
                          Edit
                        </button>
                        <button className="btn btn-danger btn-sm" onClick={() => handleDelete(book.id)}>
                          Delete
                        </button>
                      </>
                    ) : (
                      <span>View only</span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="d-flex justify-content-between">
            <button disabled={page === 1} onClick={() => setPage(page - 1)} className="btn btn-secondary">
              Previous
            </button>
            <span>Page {page} of {totalPages}</span>
            <button disabled={page === totalPages} onClick={() => setPage(page + 1)} className="btn btn-secondary">
              Next
            </button>
          </div>
        </>
      )}
      {editingBook && (
        <EditBook book={editingBook} onClose={() => setEditingBook(null)} onSave={fetchBooks} />
      )}
    </div>
  );
};

export default ManageBooks;
