import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const EditBook = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [title, setTitle] = useState('');
  const [authorName, setAuthorName] = useState('');
  const [genre, setGenre] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);

  // Fetch book details on mount
  useEffect(() => {
    const fetchBook = async () => {
      try {
        const response = await fetch(`http://localhost/book-management-system/books/get.php?id=${id}`);
        const data = await response.json();
        if (response.ok && data.book) {
          setTitle(data.book.title || '');
          setAuthorName(data.book.author_name || '');
          setGenre(data.book.genre || '');
        } 
      } catch (err) {
        setMessage('Error fetching book details.');
      } finally {
        setLoading(false);
      }
    };
    fetchBook();
  }, [id]);

  // Example validation function
  const validateBook = ({ title, author_name, genre }) => {
    if (!title || title.length < 2) return 'Title must be at least 2 characters.';
    if (!author_name || author_name.length < 2) return 'Author name must be at least 2 characters.';
    if (!genre || genre.length < 2) return 'Genre must be at least 2 characters.';
    return '';
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('book_id', id);
    formData.append('title', title);
    formData.append('author_name', authorName);
    formData.append('genre', genre);

    // Validation
    const errorMsg = validateBook(formData); // or { title, author_name, genre }
    if (errorMsg) {
      setMessage(errorMsg);
      return;
    }

    try {
      const response = await fetch('http://localhost/book-management-system/books/edit.php', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (data.status === 'success') {
        setMessage('Book updated successfully!');
        setTimeout(() => navigate('/manage-books'), 1000); // Redirect after update
      } else {
        setMessage(data.message);
      }
    } catch (error) {
      setMessage('An error occurred.');
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="container mt-5" style={{ maxWidth: '500px' }}>
      <h2>Edit Book</h2>
      {message && <div className="alert alert-info">{message}</div>}
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Title:</label>
          <input
            type="text"
            className="form-control"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label>Author Name:</label>
          <input
            type="text"
            className="form-control"
            value={authorName}
            onChange={(e) => setAuthorName(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label>Genre:</label>
          <input
            type="text"
            className="form-control"
            value={genre}
            onChange={(e) => setGenre(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Update</button>
        <button type="button" className="btn btn-secondary ms-2" onClick={() => navigate('/manage-books')}>Cancel</button>
      </form>
    </div>
  );
};

export default EditBook;
