import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Create refs for the input fields
  const emailRef = useRef(null);
  const passwordRef = useRef(null);
  const phoneRef = useRef(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Frontend validation with focus
    if (!formData.email) {
      setError('Email is required.');
      emailRef.current.focus();
      return;
    }
    if (!formData.password) {
      setError('Password is required.');
      passwordRef.current.focus();
      return;
    }

    const phoneRegex = /^\+(\d{1,4})-(\d{10})$/;

    if (!phoneRegex.test(formData.phone)) {
      setError('Phone number must include a country code and exactly 10 digits (e.g., +91-9876543210).');
      phoneRef.current && phoneRef.current.focus();
      return;
    }

    try {
      const response = await fetch('http://localhost/book-management-system/server/auth/login.php', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const result = await response.json();
      console.log('Result:', result);

      if (response.ok && result.message === 'Login successful') {
          localStorage.setItem('user_name', result.user_name);
          localStorage.setItem('user_id', result.user_id); // ✅ Store user_id
          navigate('/');
        }
 else {
        setError(result.message || 'Login failed. Please check your credentials.');
        // Focus on the first field if login fails
        emailRef.current.focus();
      }
    } catch (err) {
      console.error('Error:', err);
      setError('Server error. Please try again later.');
    }
  };

  return (
    <div className="container mt-5" style={{ maxWidth: '400px' }}>
      <h2 className="text-center mb-4">Login</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Email</label>
          <input
            ref={emailRef}
            type="email"
            name="email"
            className="form-control"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label>Password</label>
          <input
            ref={passwordRef}
            type="password"
            name="password"
            className="form-control"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label>Phone</label>
          <input
            ref={phoneRef}
            type="tel"
            name="phone"
            className="form-control"
            value={formData.phone}
            onChange={handleChange}
            placeholder="+91-9876543210"
            required
          />
        </div>
        <button type="submit" className="btn btn-primary w-100">Login</button>
      </form>
    </div>
  );
};

export default Login;
