import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import EditBook from './EditBook'; // Import the EditBook component

const ManageBooks = () => {
  const [books, setBooks] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [editingBook, setEditingBook] = useState(null);
  const [sortBy, setSortBy] = useState('title');
  const [sortOrder, setSortOrder] = useState('asc');

  const userId = localStorage.getItem('user_id'); // Assume the user ID is stored in localStorage

  // Update fetchBooks to include sorting
  useEffect(() => {
    const fetchBooks = async () => {
      setLoading(true);
      try {
        const response = await fetch(
          `http://localhost/book-management-system/books/get.php?page=${page}&sortBy=${sortBy}&sortOrder=${sortOrder}`
        );
        const data = await response.json();
        setBooks(data.books);
        setTotalPages(data.totalPages);
      } catch (err) {
        setError('Failed to fetch books');
      }
      setLoading(false);
    };

    fetchBooks();
  }, [page, sortBy, sortOrder]);

  const handleDelete = async (bookTitle) => {
    if (window.confirm('Are you sure you want to delete this book?')) {
      try {
        const response = await fetch('http://localhost/book-management-system/books/delete.php', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ name: bookTitle }), // Pass the book title as 'name'
        });

        const result = await response.json();
        if (response.ok && result.status === 'success') {
          alert('Book deleted successfully');
          setBooks(books.filter(book => book.title !== bookTitle)); // Remove deleted book from the state
        } else {
          alert('Failed to delete book');
        }
      } catch (err) {
        alert('Error deleting book');
      }
    }
  };

  const handleEditClick = (book) => {
    setEditingBook(book); // Set the book to edit
  };

  const handleBookUpdated = () => {
    // Refresh your books list here
    // fetchBooks();
  };

  const handleCloseEdit = () => {
    setEditingBook(null); // Close the modal
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4">Manage Books</h2> {/* Add margin-bottom */}
      
      <div className="mb-4">
        <Link to="/add-book" className="btn btn-success">
          Add Book
        </Link>
      </div>

      <div className="mb-4 d-flex gap-2 align-items-center">
        <label>Sort by:</label>
        <select value={sortBy} onChange={e => setSortBy(e.target.value)} className="form-select w-auto">
          <option value="title">Title</option>
          <option value="author_name">Author</option>
          <option value="genre">Genre</option>
        </select>
        <select value={sortOrder} onChange={e => setSortOrder(e.target.value)} className="form-select w-auto">
          <option value="asc">Ascending</option>
          <option value="desc">Descending</option>
        </select>
      </div>

      {loading ? (
        <p>Loading books...</p>
      ) : (
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Title</th>
              <th>Author</th>
              <th>Genre</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {books.map((book) => (
              <tr key={book.id}>
                <td>{book.title}</td>
                <td>{book.author_name}</td>
                <td>{book.genre}</td>
                <td>
                  <Link to={`/edit-book/${book.id}`} className="btn btn-warning btn-sm">Edit</Link>
                  <button 
                    onClick={() => handleDelete(book.title)} // Pass title instead of id
                    className="btn btn-danger btn-sm ms-2">
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <div className="pagination d-flex justify-content-center">
        <button 
          className="btn btn-primary" 
          disabled={page === 1} 
          onClick={() => setPage(page - 1)}>
          Previous
        </button>
        <span className="mx-2">Page {page} of {totalPages}</span>
        <button 
          className="btn btn-primary" 
          disabled={page === totalPages} 
          onClick={() => setPage(page + 1)}>
          Next
        </button>
      </div>

      {/* Show EditBook modal if editingBook is set */}
      {editingBook && (
        <EditBook
          book={editingBook}
          onClose={handleCloseEdit}
          onBookUpdated={handleBookUpdated}
        />
      )}
    </div>
  );
};

export default ManageBooks;
